const chai = require('chai');
const chaiHttp = require('chai-http');
const { app } = require('../app');  // Assurez-vous d'ajuster le chemin en fonction de votre structure

chai.use(chaiHttp);

const expect = chai.expect;

describe('Livre Routes', () => {
  it('devrait récupérer la liste des livres', (done) => {
    chai.request(app)
      .get('/recupListeLivres')
      .end((err, res) => {
        expect(res).to.have.status(200);
        expect(res.body).to.be.an('array');  // Vérifiez que la réponse est un tableau
        // Vous pouvez ajouter des assertions supplémentaires en fonction de votre implémentation

        done();
      });
  });
});
